create table company_registered (id int4 generated by default as identity, adress varchar(255), city varchar(255), district varchar(255), name varchar(255), name_legal_representative varchar(255), name_supervisor varchar(255), office_legal_representative varchar(255), office_supervisor varchar(255), phone_number varchar(255), state varchar(255), status_company int4, zip_code varchar(255), primary key (id));
create table company_unregistered (id int4 generated by default as identity, adress varchar(255), city varchar(255), district varchar(255), name varchar(255), name_legal_representative varchar(255), name_supervisor varchar(255), office_legal_representative varchar(255), office_supervisor varchar(255), phone_number varchar(255), state varchar(255), status_company int4, zip_code varchar(255), primary key (id));
create table intern (id int4 generated by default as identity, address varchar(255), city varchar(255), course varchar(255), district varchar(255), name varchar(255), phone_number varchar(255), state varchar(255), zip_code varchar(255), primary key (id));
create table internship (id int4 generated by default as identity, activities_plan varchar(255), daily_workload int4, end_validity timestamp, interval int4, journey_start_time timestamp, modality varchar(255), name_teacher varchar(255), paid boolean, start_validity timestamp, status_internship varchar(255), type varchar(255), value_paid float8, weekly_workload int4, intern_id int4, registered_company_registered_id int4, unregistered_company_id int4, primary key (id));
create table observation (id int4 generated by default as identity, observation varchar(255), internship_id int4, user_id int4, primary key (id));
create table student (id int4 generated by default as identity, internship_id int4, user_id int4, primary key (id));
create table tb_user (id int4 generated by default as identity, email varchar(255), name varchar(255), password varchar(255), permission_type varchar(255), primary key (id));
