alter table if exists internship add constraint FKotiw07q5yhqemkw1ct33vfbi0 foreign key (unregistered_company_id) references company_unregistered;
alter table if exists observation add constraint FK1d6aa7k6ejt6kacap1mng4hl9 foreign key (internship_id) references internship;
alter table if exists observation add constraint FKnprtuqakhhaf5byrv27ar311l foreign key (user_id) references tb_user;
